package test;

import java.util.ArrayList;
import java.util.List;

public class ListDemo {
public static void main(String[] args) {
List list=new ArrayList();
for(int i=0;i&lt;3;i++){
User u=new User(i,"第"+i+"号",20+i);
list.add(u);
}
System.out.println(list);
for(User u:list){
u.setName("一样的名字");
}
System.out.println(list);
}
